import React, {Component} from 'react';
import './Counter.css'

class Counter extends Component {
    constructor() {
        super();

        this.state = {
            counter: 0
        }
       
        this.increament = this.increament.bind(this)
        this.decrement = this.decrement.bind(this)

    }

    render() {
        return (
            <div className="counter">
                <div>
                    <button className="" onClick={this.increament}>+1</button>
                    <button className="" onClick={this.decrement}>-1</button>
                </div>
                <div>
                    <button className="" onClick={this.increament5}>+5</button>
                    <button className="" onClick={this.decrement5}>-5</button>
                </div>
                <div>
                    <button className="" onClick={this.increament10}>+10</button>
                    <button className="" onClick={this.decrement10}>-10</button>
                </div>
                
                <div className="count">{this.state.counter}</div>
                <div><button className="btn-reset">Reset</button></div>
            </div>
        );
    }

    increament() {
        // console.log('increament');
        this.setState({
            state: this.state.counter++
        })
    }

    decrement() {
        this.setState({
            state: this.state.counter--
        })
    }

}



export default Counter
