import Axios from "axios";
import { API_URL } from '../../api/todo/Constants.js';

class TodoDataService {
    findAllTodo = () => {
        return Axios.get(`${API_URL}/users/todos`)
    }

    deleteTodoById = (id) => {
        return Axios.delete(`${API_URL}/delete/todos/${id}`)
    }

    editTodo = (id) => {
        return Axios.get(`${API_URL}/edit/todos/${id}`)
    }

    updateTodo = (id, todo) => {
        return Axios.put(`${API_URL}/update/todos/${id}`, todo)
    }

    postTodo = (todo) => {
        return Axios.post(`${API_URL}/post/todos`, todo)
    }

}

export default new TodoDataService()
