import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom';

class TodoApp extends Component {
    render() {
        return (
            <div className="TodoApp">
                <Router>
                    <HeaderComponent />
                    <Switch>
                        <Route path="/" exact component={LoginComponent} />
                        <Route path="/login" component={LoginComponent} />
                        <Route path="/welcome/:name" component={WelcomeComponent} />
                        <Route path="/todos" component={ListTodosComponent} />
                        <Route path="/logout" component={LogoutComponent} />
                        <Route component={ErrorComponent} />
                    </Switch>
                    <FooterComponent />
                </Router>
               {/* <LoginComponent />
               <WelcomeComponent /> */}
            </div>
        );
    }
}

class HeaderComponent extends Component {
    render() {
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a className="navbar-brand" href="http://www.in28minutes.com">in28minutes</a></div>
                    <ul className="navbar-nav">
                        <li className="nav-link"><Link to="/welcome/in28minutes">Home</Link></li>
                        <li className="nav-link"><Link to="/todos">Todos</Link></li>
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li className="nav-link"><Link to="/login">Login</Link></li>
                        <li className="nav-link"><Link to="/logout">Logout</Link></li>
                    </ul>
                </nav>
            </header>
            // <div className="HeaderComponent">
            //     Head component <hr/>
            // </div>
        );  
    }
}

class FooterComponent extends Component {
    render() {
        const date = new Date()
        const year = date.getFullYear
        return (
            <footer className="FooterComponent">
                <span className="text-muted">All Rights Reserved {year} in28munites </span>
            </footer>
        );  
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <div className="LogoutComponent">
                <h1>Logged out</h1>
                <div className="container">
                    Thank you for using our application
                </div>
            </div>
        );  
    }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <div className="WelcomeComponent">
                Welcome {this.props.match.params.name}
                {' '}Todo Lists click <Link to="/todos">here</Link>
            </div>
        );
    }
}

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos:
            [
                {id: 1, description: 'Learn React', done: false, targitDate: new Date()},
                {id: 2, description: 'Lear Springboot', done: false, targitDate: new Date()},
                {id: 3, description: 'Lear Javascript', done: false, targitDate: new Date()}
            ]   
        }
    }

    render() {
        return (
            <div className="ListTodosComponent">
                <h1>List Todos</h1>
                <table>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>description</th>
                            <th>isDone?</th>
                            <th>date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map(
                                todo => 
                                <tr>
                                    <td>{todo.id}</td>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targitDate.toString()}</td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        );
    }
}

function ErrorComponent() {
    return <div style={{fontSize: "50px", color: "red"}}>Page not found</div>
}

class LoginComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            username: 'in28minutes',
            password: '',
            hasLoginFailed : false,
            showSuccessMessage: false
        }

        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    handleChange(e) {
        // console.log(this.state)
        this.setState(
            {
                [e.target.name]: e.target.value
            }
        )
    }

    loginClicked() {
        if(this.state.username === 'in28minutes' && this.state.password === 'dummy') {
            this.props.history.push(`/welcome/${this.state.username}`)
            //this.setState({showSuccessMessage: true})
            //this.setState({hasLoginFailed: false})
        } else {
            this.setState({showSuccessMessage: false})
            this.setState({hasLoginFailed: true})
        }
    }

    render() {
        return (
            <div className="LoginComponent">
                {/*<ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed} />
                <ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage} />*/}
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showSuccessMessage && <div>Login successful</div>}
                Username: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                <button className="" onClick={this.loginClicked}>Login</button>
            </div>
        );
    }
}

// function  ShowInvalidCredentials(props) {
//     if(props.hasLoginFailed) {
//         return <div>Invalid Credentials</div>
//     }
//     return null
// }

// function ShowLoginSuccessMessage(props) {
//     if(props.showSuccessMessage) {
//         return <div>Login successful</div>
//     }
//     return null
// }

export default TodoApp
